mapper.select:
select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}


order by create_time desc

select * from setmeal

     and name like concat('%',#{name},'%') 
     and category_id = #{categoryId} 
     and status = #{status}

select count(id) from setmeal

     and status = #{status} 
     and category_id = #{categoryId}
